import React from "react";

function Searchbar(props) {
    return (
        <form>
            <div className="form-group">

                <input className="form-control" placeholder="Search for an employee" 
                    value = {props.search}
                    onChange = {props.handleSearchChange}
                    type= "text"
                />
            </div>

            <div className="mb-2">Sort: {" "}
                <button onClick={sortAscending} type="button"className="btn btn-secondary" style={{backgroundColor: "#DCEEE4",color: "black"}}>A-Z</button> {""}
                <button onClick={sortDescending} type="button" className="btn btn-secondary" style={{backgroundColor: "#DCEEE4", color: "black"}}>Z-A</button> 
            </div>
        </form>
    )
}

export default Searchbar;
